import React, { useState, useEffect, useRef } from 'react';
import '../../styles/HomePageCSS/TestimonialsSection.css';

// Import ảnh đại diện cho khách du lịch
import avatar1 from '../../assets/images/avatar-1.jpg'; 
import avatar2 from '../../assets/images/avatar-2.jpg';
import avatar3 from '../../assets/images/avatar-3.jpg';
import avatar4 from '../../assets/images/avatar-4.jpg';

// Import hình ảnh background
import bgImage from '../../assets/images/background_BX_2.jpg';

const TestimonialsSection = () => {
    const [activeIndex, setActiveIndex] = useState(0);
    const [isManualChange, setIsManualChange] = useState(false);
    const [screenWidth, setScreenWidth] = useState(window.innerWidth);
    const autoPlayRef = useRef(null);
    const sliderRef = useRef(null);
    
    const testimonials = [
        {
            id: 1,
            author: "Nguyễn Thanh Hà",
            position: "Blogger Du lịch",
            avatar: avatar1,
            rating: 5,
            content: "Phú Yên là một địa điểm du lịch tuyệt vời với những bãi biển hoang sơ, đặc biệt là Bãi Xép - nơi tôi đã có những bức ảnh đẹp nhất trong chuyến đi. Ẩm thực địa phương phong phú, hải sản tươi ngon và giá cả phải chăng.",
            date: "Tháng 3, 2023",
            tripType: "Du lịch gia đình"
        },
        {
            id: 2,
            author: "Trần Minh Đức",
            position: "Nhiếp ảnh gia",
            avatar: avatar2,
            rating: 5,
            content: "Gành Đá Đĩa là một kỳ quan thiên nhiên độc đáo mà bất kỳ ai đến Phú Yên đều phải ghé thăm. Những khối đá xếp chồng lên nhau tạo nên một khung cảnh ngoạn mục, đặc biệt khi hoàng hôn. Tôi đã có những tấm hình tuyệt vời tại đây!",
            date: "Tháng 5, 2023",
            tripType: "Du lịch nhiếp ảnh"
        },
        {
            id: 3,
            author: "Lê Thị Phương",
            position: "Doanh nhân",
            avatar: avatar3,
            rating: 4,
            content: "Chuyến đi Phú Yên là một quyết định tuyệt vời cho kỳ nghỉ ngắn ngày của gia đình tôi. Con tôi thích thú với biển Bãi Xép và những hoạt động thú vị tại đây. Mọi người đều rất thân thiện và nhiệt tình giúp đỡ du khách.",
            date: "Tháng 2, 2023",
            tripType: "Du lịch cuối tuần"
        },
        {
            id: 4,
            author: "Hoàng Văn Nam",
            position: "Kỹ sư phần mềm",
            avatar: avatar4,
            rating: 5,
            content: "Mũi Điện với ngọn hải đăng cổ là điểm đến không thể bỏ qua khi đến Phú Yên. Được đón bình minh đầu tiên trên đất liền tại đây là một trải nghiệm tuyệt vời. Nhà nghỉ ở đây sạch sẽ, giá cả hợp lý và dịch vụ rất tốt.",
            date: "Tháng 7, 2023",
            tripType: "Du lịch phượt"
        }
    ];

    // Theo dõi kích thước màn hình
    useEffect(() => {
        const handleResize = () => {
            setScreenWidth(window.innerWidth);
        };

        window.addEventListener('resize', handleResize);
        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    // Thiết lập auto play
    useEffect(() => {
        const playNext = () => {
            if (!isManualChange) {
                setActiveIndex((prev) => (prev + 1) % testimonials.length);
            }
            setIsManualChange(false);
        };

        autoPlayRef.current = setTimeout(playNext, 5000);

        return () => {
            if (autoPlayRef.current) {
                clearTimeout(autoPlayRef.current);
            }
        };
    }, [activeIndex, isManualChange, testimonials.length]);

    // Xử lý chuyển đổi slide
    const handleNext = () => {
        setActiveIndex((prev) => (prev + 1) % testimonials.length);
        setIsManualChange(true);
        if (autoPlayRef.current) {
            clearTimeout(autoPlayRef.current);
        }
    };

    const handlePrev = () => {
        setActiveIndex((prev) => (prev - 1 + testimonials.length) % testimonials.length);
        setIsManualChange(true);
        if (autoPlayRef.current) {
            clearTimeout(autoPlayRef.current);
        }
    };

    const handleDotClick = (index) => {
        setActiveIndex(index);
        setIsManualChange(true);
        if (autoPlayRef.current) {
            clearTimeout(autoPlayRef.current);
        }
    };

    // Render dấu sao đánh giá
    const renderStars = (rating) => {
        return (
            <div className="testimonial-rating-v2">
                {[...Array(5)].map((_, i) => (
                    <i 
                        key={i} 
                        className={`bi ${i < rating ? 'bi-star-fill' : 'bi-star'}`}
                    ></i>
                ))}
            </div>
        );
    };

    // Hiển thị nhiều card trên màn hình lớn
    const renderVisibleCards = () => {
        if (screenWidth >= 992) {
            // Tạo mảng các testimonials hiển thị song song
            const visibleCards = [];
            for (let i = 0; i < testimonials.length; i++) {
                // Lấy index của 3 card liên tiếp (active-1, active, active+1)
                const cardIndex = (activeIndex - 1 + i + testimonials.length) % testimonials.length;
                let cardClassName = "testimonial-card";
                
                // Card ở vị trí giữa là active
                if (i === 1) cardClassName += " active";
                else if (i === 0) cardClassName += " prev";
                else if (i === 2) cardClassName += " next";
                else cardClassName += " hidden";

                visibleCards.push(
                    <div 
                        key={testimonials[cardIndex].id}
                        className={cardClassName}
                    >
                        <div className="card-inner">
                            <div className="card-header">
                                <div className="user-info">
                                    <div className="user-avatar">
                                        <img src={testimonials[cardIndex].avatar} alt={testimonials[cardIndex].author} />
                                    </div>
                                    <div>
                                        <h4 className="user-name">{testimonials[cardIndex].author}</h4>
                                        <p className="user-position">{testimonials[cardIndex].position}</p>
                                    </div>
                                </div>
                                <div className="trip-badge">
                                    <span>{testimonials[cardIndex].tripType}</span>
                                </div>
                            </div>
                            
                            <div className="card-body">
                                <div className="quote-mark">
                                    <i className="bi bi-quote"></i>
                                </div>
                                <p className="review-text">{testimonials[cardIndex].content}</p>
                            </div>
                            
                            <div className="card-footer">
                                <div className="rating-container">
                                    {renderStars(testimonials[cardIndex].rating)}
                                    <span className="rating-score">{testimonials[cardIndex].rating.toFixed(1)}</span>
                                </div>
                                <div className="review-date">
                                    <i className="bi bi-calendar3"></i>
                                    <span>{testimonials[cardIndex].date}</span>
                                </div>
                            </div>
                        </div>
                    </div>
                );
            }
            return visibleCards;
        } else {
            // Trên màn hình nhỏ, chỉ hiển thị card active
            return testimonials.map((item, index) => (
                <div 
                    key={item.id}
                    className={`testimonial-card ${index === activeIndex ? 'active' : ''}`}
                >
                    <div className="card-inner">
                        <div className="card-header">
                            <div className="user-info">
                                <div className="user-avatar">
                                    <img src={item.avatar} alt={item.author} />
                                </div>
                                <div>
                                    <h4 className="user-name">{item.author}</h4>
                                    <p className="user-position">{item.position}</p>
                                </div>
                            </div>
                            <div className="trip-badge">
                                <span>{item.tripType}</span>
                            </div>
                        </div>
                        
                        <div className="card-body">
                            <div className="quote-mark">
                                <i className="bi bi-quote"></i>
                            </div>
                            <p className="review-text">{item.content}</p>
                        </div>
                        
                        <div className="card-footer">
                            <div className="rating-container">
                                {renderStars(item.rating)}
                                <span className="rating-score">{item.rating.toFixed(1)}</span>
                            </div>
                            <div className="review-date">
                                <i className="bi bi-calendar3"></i>
                                <span>{item.date}</span>
                            </div>
                        </div>
                    </div>
                </div>
            ));
        }
    };

    return (
        <section className="testimonials-section-v2" style={{ backgroundImage: `url(${bgImage})` }}>
            <div className="testimonial-overlay"></div>
            
            <div className="container position-relative">
                <div className="section-header">
                    <div className="header-badge">ĐÁNH GIÁ & CẢM NHẬN</div>
                    <h2 className="section-title">
                        Trải nghiệm khó quên <br />
                        <span className="highlight">tại Phú Yên</span>
                    </h2>
                    <p className="section-description">
                        Khám phá những trải nghiệm và cảm nhận chân thực từ khách du lịch đã ghé thăm Phú Yên
                    </p>
                </div>
                
                <div className="testimonials-container" ref={sliderRef}>
                    <div className="testimonials-wrapper-v2">
                        {renderVisibleCards()}
                    </div>
                    
                    <div className="testimonials-navigation">
                        <div className="navigation-dots">
                            {testimonials.map((_, index) => (
                                <button 
                                    key={index}
                                    className={`dot-indicator ${index === activeIndex ? 'active' : ''}`}
                                    onClick={() => handleDotClick(index)}
                                    aria-label={`Go to slide ${index + 1}`}
                                />
                            ))}
                        </div>
                        
                        <div className="navigation-arrows">
                            <button 
                                className="nav-arrow prev-arrow" 
                                onClick={handlePrev}
                                aria-label="Previous testimonial"
                            >
                                <i className="bi bi-arrow-left"></i>
                            </button>
                            <button 
                                className="nav-arrow next-arrow" 
                                onClick={handleNext}
                                aria-label="Next testimonial"
                            >
                                <i className="bi bi-arrow-right"></i>
                            </button>
                        </div>
                    </div>
                </div>
                
                <div className="testimonial-summary">
                    <div className="summary-item">
                        <div className="summary-icon">
                            <i className="bi bi-star"></i>
                        </div>
                        <div className="summary-content">
                            <h3>4.8<span>+</span></h3>
                            <p>Đánh giá trung bình</p>
                        </div>
                    </div>
                    
                    <div className="summary-item">
                        <div className="summary-icon">
                            <i className="bi bi-people"></i>
                        </div>
                        <div className="summary-content">
                            <h3>150<span>+</span></h3>
                            <p>Khách hàng hài lòng</p>
                        </div>
                    </div>
                    
                    <div className="summary-item">
                        <div className="summary-icon">
                            <i className="bi bi-compass"></i>
                        </div>
                        <div className="summary-content">
                            <h3>25<span>+</span></h3>
                            <p>Điểm đến hấp dẫn</p>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
};

export default TestimonialsSection;
