import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import '../styles/LocationCSS/LocationListPage.css';

const LocationListPage = () => {
  const [locations, setLocations] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchLocations = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/locations');
        setLocations(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching locations:', error);
        setLoading(false);
      }
    };

    fetchLocations();
  }, []);

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">
          <i className="bi bi-arrow-repeat spinning"></i>
          <span>Đang tải dữ liệu...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="locations-page">
      <section className="locations-hero">
        <div className="hero-content">
          <h1>Khám phá Phú Yên</h1>
          <p>Điểm đến tuyệt vời cho hành trình của bạn</p>
        </div>
      </section>

      <div className="container">
        <div className="row">
          {locations && locations.map((location) => (
            <div key={location._id} className="col-lg-4 col-md-6 mb-4">
              <div className="location-card">
                <div className="location-image">
                  {location.images && location.images.length > 0 && (
                    <img 
                      src={`http://localhost:5000${location.images[0]}`}
                      alt={location.title || 'Location image'}
                    />
                  )}
                  <div className="location-rating">
                    <i className="bi bi-star-fill"></i>
                    <span>{location.rating || 'N/A'}</span>
                  </div>
                </div>
                <div className="location-content">
                  <h3>
                    <Link to={`/locations/${location._id}`}>{location.title}</Link>
                  </h3>
                  <div className="location-meta">
                    <span>
                      <i className="bi bi-geo-alt"></i>
                      {location.address || 'Chưa có địa chỉ'}
                    </span>
                  </div>
                  <p className="location-description">
                    {location.description || 'Chưa có mô tả'}
                  </p>
                  <div className="location-footer">
                    <Link to={`/locations/${location._id}`} className="btn-details">
                      Xem chi tiết <i className="bi bi-arrow-right"></i>
                    </Link>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default LocationListPage;
