// Import hình ảnh background
import bgPattern from '../../assets/images/background_GDD_3.jpg';

const TestimonialsSection = () => {
    const [activeIndex, setActiveIndex] = useState(0);
    const [isManualChange, setIsManualChange] = useState(false);
    const [isAnimating, setIsAnimating] = useState(false);
    const autoPlayRef = useRef(null);
    const sliderRef = useRef(null);
    
    const testimonials = [
        {
            id: 1,
            author: "Nguyễn Thanh Hà",
            position: "Blogger Du lịch",
            avatar: avatar1,
            rating: 5,
            content: "Phú Yên là một địa điểm du lịch tuyệt vời với những bãi biển hoang sơ, đặc biệt là Bãi Xép - nơi tôi đã có những bức ảnh đẹp nhất trong chuyến đi. Ẩm thực địa phương phong phú, hải sản tươi ngon và giá cả phải chăng.",
            date: "Tháng 3, 2025",
            location: "Bãi Xép"
        },
        {
            id: 2,
            author: "Trần Minh Đức",
            position: "Nhiếp ảnh gia",
            avatar: avatar2,
            rating: 5,
            content: "Gành Đá Đĩa là một kỳ quan thiên nhiên độc đáo mà bất kỳ ai đến Phú Yên đều phải ghé thăm. Những khối đá xếp chồng lên nhau tạo nên một khung cảnh ngoạn mục, đặc biệt khi hoàng hôn. Tôi đã có những tấm hình tuyệt vời tại đây!",
            date: "Tháng 5, 2025",
            location: "Gành Đá Đĩa"
        },
        {
            id: 3,
            author: "Lê Thị Phương",
            position: "Doanh nhân",
            avatar: avatar3,
            rating: 4,
            content: "Chuyến đi Phú Yên là một quyết định tuyệt vời cho kỳ nghỉ ngắn ngày của gia đình tôi. Con tôi thích thú với biển Bãi Xép và những hoạt động thú vị tại đây. Mọi người đều rất thân thiện và nhiệt tình giúp đỡ du khách.",
            date: "Tháng 2, 2025",
            location: "Đầm Ô Loan"
        },
        {
            id: 4,
            author: "Hoàng Văn Nam",
            position: "Kỹ sư phần mềm",
            avatar: avatar4,
            rating: 5,
            content: "Mũi Điện với ngọn hải đăng cổ là điểm đến không thể bỏ qua khi đến Phú Yên. Được đón bình minh đầu tiên trên đất liền tại đây là một trải nghiệm tuyệt vời. Nhà nghỉ ở đây sạch sẽ, giá cả hợp lý và dịch vụ rất tốt.",
            date: "Tháng 7, 2025",
            location: "Mũi Điện"
        }
    ];

    // Thiết lập auto play
    useEffect(() => {
        const playNext = () => {
            if (!isManualChange) {
                changeSlide((activeIndex + 1) % testimonials.length);
            }
            setIsManualChange(false);
        };

        autoPlayRef.current = setTimeout(playNext, 6000);

        return () => {
            if (autoPlayRef.current) {
                clearTimeout(autoPlayRef.current);
            }
        };
    }, [activeIndex, isManualChange, testimonials.length]);

    const changeSlide = (index) => {
        if (isAnimating) return;
        
        setIsAnimating(true);
        setActiveIndex(index);
        
        // Reset animation after transition completes
        setTimeout(() => {
            setIsAnimating(false);
        }, 700);
    };

    // Xử lý chuyển đổi slide
    const handleNext = () => {
        changeSlide((activeIndex + 1) % testimonials.length);
        setIsManualChange(true);
        if (autoPlayRef.current) {
            clearTimeout(autoPlayRef.current);
        }
    };

    const handlePrev = () => {
        changeSlide((activeIndex - 1 + testimonials.length) % testimonials.length);
        setIsManualChange(true);
        if (autoPlayRef.current) {
            clearTimeout(autoPlayRef.current);
        }
    };

    const handleDotClick = (index) => {
        changeSlide(index);
        setIsManualChange(true);
        if (autoPlayRef.current) {
            clearTimeout(autoPlayRef.current);
        }
    };

    // Render dấu sao đánh giá
    const renderStars = (rating) => {
        return (
            <div className="testimonial-rating">
                {[...Array(5)].map((_, i) => (
                    <i 
                        key={i} 
                        className={`bi ${i < rating ? 'bi-star-fill' : 'bi-star'}`}
                    ></i>
                ))}
            </div>
        );
    };

    return (
        <section className="testimonials-section-v2">
            <div className="testimonial-bg">
                <div className="bg-pattern" style={{ backgroundImage: `url(${bgPattern})` }}></div>
                <div className="bg-overlay"></div>
            </div>
            
            <div className="container position-relative">
                <div className="row align-items-center">
                    <div className="col-lg-5">
                        <div className="testimonials-header">
                            <div className="section-badge">
                                <span><i className="bi bi-chat-quote-fill"></i>Đánh giá</span>
                            </div>
                            <h2 className="section-title">Trải nghiệm <br/><span>Đáng nhớ</span> từ khách hàng</h2>
                            <p className="section-description">
                                Khám phá những trải nghiệm thực tế và cảm nhận chân thực từ du khách đã đến thăm Phú Yên và trải nghiệm dịch vụ của chúng tôi.
                            </p>
                            
                            <div className="testimonial-stats-wrapper">
                                <div className="testimonial-stat">
                                    <div className="stat-number"><span className="counter">4.9</span><small>+</small></div>
                                    <div className="stat-label">Đánh giá trung bình</div>
                                </div>
                                <div className="divider"></div>
                                <div className="testimonial-stat">
                                    <div className="stat-number"><span className="counter">150</span><small>+</small></div>
                                    <div className="stat-label">Khách hàng hài lòng</div>
                                </div>
                            </div>
                            
                            <div className="testimonial-controls">
                                <div className="slider-nav">
                                    <button 
                                        className="nav-button prev-button" 
                                        onClick={handlePrev}
                                        disabled={isAnimating}
                                    >
                                        <i className="bi bi-arrow-left"></i>
                                    </button>
                                    <button 
                                        className="nav-button next-button" 
                                        onClick={handleNext}
                                        disabled={isAnimating}
                                    >
                                        <i className="bi bi-arrow-right"></i>
                                    </button>
                                </div>
                                
                                <div className="slider-dots">
                                    {testimonials.map((_, index) => (
                                        <button 
                                            key={index} 
                                            className={`dot ${index === activeIndex ? 'active' : ''}`}
                                            onClick={() => handleDotClick(index)}
                                        ></button>
                                    ))}
                                </div>
                            </div>
                        </div>
                    </div>
                    
                    <div className="col-lg-7">
                        <div className="testimonials-slider-container">
                            <div 
                                className="testimonials-slider" 
                                ref={sliderRef}
                            >
                                {testimonials.map((item, index) => (
                                    <div 
                                        key={item.id} 
                                        className={`testimonial-card ${activeIndex === index ? 'active' : ''}`}
                                    >
                                        <div className="card-decoration">
                                            <div className="quote-shape">
                                                <i className="bi bi-quote"></i>
                                            </div>
                                        </div>
                                        
                                        <div className="testimonial-header">
                                            <div className="author-info">
                                                <div className="author-avatar">
                                                    <img src={item.avatar} alt={item.author} />
                                                </div>
                                                <div className="author-details">
                                                    <h4 className="author-name">{item.author}</h4>
                                                    <p className="author-title">{item.position}</p>
                                                </div>
                                            </div>
                                            
                                            <div className="testimonial-location">
                                                <i className="bi bi-geo-alt-fill"></i>
                                                <span>{item.location}</span>
                                            </div>
                                        </div>
                                        
                                        <div className="testimonial-body">
                                            <p className="testimonial-text">{item.content}</p>
                                        </div>
                                        
                                        <div className="testimonial-footer">
                                            <div className="rating-wrapper">
                                                {renderStars(item.rating)}
                                                <span className="rating-score">{item.rating.toFixed(1)}/5</span>
                                            </div>
                                            <div className="testimonial-date">
                                                <i className="bi bi-calendar3"></i>
                                                <span>{item.date}</span>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                            
                            <div className="slider-background-element"></div>
                        </div>
                    </div>
                </div>
                
                <div className="trusted-brands">
                    <h3 className="brands-title">Được tin tưởng bởi</h3>
                    <div className="brands-logos">
                        <div className="brand-logo">
                            <i className="bi bi-slack"></i>
                            <span>Vietravel</span>
                        </div>
                        <div className="brand-logo">
                            <i className="bi bi-microsoft"></i>
                            <span>Saigontourist</span>
                        </div>
                        <div className="brand-logo">
                            <i className="bi bi-google"></i>
                            <span>Vietnam Airlines</span>
                        </div>
                        <div className="brand-logo">
                            <i className="bi bi-wordpress"></i>
                            <span>Booking.com</span>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
};

export default TestimonialsSection;
