import React from 'react';
import { Link } from 'react-router-dom';
import '../../styles/HomePageCSS/HeroSection.css'; // Đường dẫn đã được sửa

const HeroSection = () => {
    return (
        <section className="hero-section">
            <div className="hero-content">
                <h1 className="hero-title">PHÚ YÊN</h1>
                <p className="hero-subtitle">Xứ sở hoa vàng cỏ xanh</p>
                <Link to="/locations" className="btn btn-secondary hero-btn">
                    Khám phá ngay
                </Link>
            </div>
            <div className="wave-divider">
                <svg data-name="Layer 1" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1200 120" preserveAspectRatio="none">
                    <path d="M321.39,56.44c58-10.79,114.16-30.13,172-41.86,82.39-16.72,168.19-17.73,250.45-.39C823.78,31,906.67,72,985.66,92.83c70.05,18.48,146.53,26.09,214.34,3V0H0V27.35A600.21,600.21,0,0,0,321.39,56.44Z" className="shape-fill"></path>
                </svg>
            </div>
        </section>
    );
};

export default HeroSection;
