import React, { useState, useEffect, useRef } from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../../styles/show/header.css';

const Header = ({ menuOpen, setMenuOpen }) => {
    const [scrolled, setScrolled] = useState(false);
    const [searchActive, setSearchActive] = useState(false);
    const [activeDropdown, setActiveDropdown] = useState(null);
    const location = useLocation();
    const searchInputRef = useRef(null);
    
    // Xử lý sự kiện scroll
    useEffect(() => {
        const handleScroll = () => {
            if (window.scrollY > 80) {
                setScrolled(true);
            } else {
                setScrolled(false);
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);
    
    // Focus vào input khi mở search overlay
    useEffect(() => {
        if (searchActive && searchInputRef.current) {
            searchInputRef.current.focus();
        }
    }, [searchActive]);
    
    // Đóng menu khi chuyển route
    useEffect(() => {
        setMenuOpen(false);
        setSearchActive(false);
    }, [location, setMenuOpen]);

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };
    
    const toggleSearch = () => {
        setSearchActive(!searchActive);
    };
    
    const toggleDropdown = (index) => {
        if (window.innerWidth <= 992) {
            setActiveDropdown(activeDropdown === index ? null : index);
        }
    };

    // Kiểm tra route hiện tại để highlight menu item
    const isActive = (path) => {
        return location.pathname === path ? 'active' : '';
    };

    return (
        <header className={`header ${scrolled ? 'scrolled' : ''}`}>
            <div className="top-bar">
                <div className="container">
                    <div className="contact-info">
                        <div className="info-item">
                            <i className="bi bi-telephone-fill"></i>
                            <span>+84 257 3999 666</span>
                        </div>
                        <div className="info-item">
                            <i className="bi bi-envelope-fill"></i>
                            <span>dulich@phuyen.gov.vn</span>
                        </div>
                    </div>
                    <div className="social-links">
                        <a href="https://www.facebook.com" target="_blank" rel="noopener noreferrer" className="social-icon" aria-label="Facebook">
                            <i className="bi bi-facebook"></i>
                        </a>
                        <a href="https://www.instagram.com" target="_blank" rel="noopener noreferrer" className="social-icon" aria-label="Instagram">
                            <i className="bi bi-instagram"></i>
                        </a>
                        <a href="https://www.youtube.com" target="_blank" rel="noopener noreferrer" className="social-icon" aria-label="Youtube">
                            <i className="bi bi-youtube"></i>
                        </a>
                        <a href="https://www.tiktok.com" target="_blank" rel="noopener noreferrer" className="social-icon" aria-label="Tiktok">
                            <i className="bi bi-tiktok"></i>
                        </a>
                    </div>
                </div>
            </div>

            <div className={`main-header ${scrolled ? 'compact' : ''}`}>
                <div className="container">
                    <div className="logo">
                        <Link to="/">
                            <div className="logo-wrapper">
                                <img 
                                    src="https://dulich.phuyen.gov.vn/uploads/image/2020/08/27/logophuyen.png" 
                                    alt="Phú Yên Tourism Logo" 
                                    className="logo-image"
                                />
                                <div className="logo-text">
                                    <span className="logo-title">PHÚ YÊN</span>
                                    <span className="logo-slogan">Xứ sở hoa vàng cỏ xanh</span>
                                </div>
                            </div>
                        </Link>
                    </div>

                    <button className="mobile-toggle" onClick={toggleMenu} aria-label="Toggle menu">
                        <i className={`bi ${menuOpen ? 'bi-x-lg' : 'bi-list'}`}></i>
                    </button>

                    <nav className={`main-nav ${menuOpen ? 'active' : ''}`}>
                        <ul className="nav-links">
                            <li>
                                <Link to="/" className={`nav-link ${isActive('/')}`}>
                                    <span className="nav-icon"><i className="bi bi-house-door"></i></span>
                                    <span className="nav-text">Trang chủ</span>
                                </Link>
                            </li>
                            <li className={`dropdown ${activeDropdown === 0 ? 'show' : ''}`}>
                                <Link 
                                    to="/locations" 
                                    className={`nav-link ${isActive('/locations')}`}
                                    onClick={(e) => {
                                        if (window.innerWidth <= 992) {
                                            e.preventDefault();
                                            toggleDropdown(0);
                                        }
                                    }}
                                >
                                    <span className="nav-icon"><i className="bi bi-geo-alt"></i></span>
                                    <span className="nav-text">Điểm đến</span>
                                    <i className="bi bi-chevron-down dropdown-indicator"></i>
                                </Link>
                                <div className="dropdown-menu">
                                    <Link to="/locations/beaches" className="dropdown-item">Bãi biển</Link>
                                    <Link to="/locations/mountains" className="dropdown-item">Núi & Đồi</Link>
                                    <Link to="/locations/cultural" className="dropdown-item">Di tích văn hóa</Link>
                                    <Link to="/locations/specialties" className="dropdown-item">Đặc sản</Link>
                                </div>
                            </li>
                            <li>
                                <Link to="/plan" className={`nav-link ${isActive('/plan')}`}>
                                    <span className="nav-icon"><i className="bi bi-calendar-event"></i></span>
                                    <span className="nav-text">Lập kế hoạch</span>
                                </Link>
                            </li>
                            <li>
                                <Link to="/about" className={`nav-link ${isActive('/about')}`}>
                                    <span className="nav-icon"><i className="bi bi-info-circle"></i></span>
                                    <span className="nav-text">Giới thiệu</span>
                                </Link>
                            </li>
                            <li>
                                <Link to="/contact" className={`nav-link ${isActive('/contact')}`}>
                                    <span className="nav-icon"><i className="bi bi-chat-dots"></i></span>
                                    <span className="nav-text">Liên hệ</span>
                                </Link>
                            </li>
                        </ul>
                        <div className="nav-buttons">
                            <button className="search-btn" onClick={toggleSearch} aria-label="Search">
                                <i className="bi bi-search"></i>
                            </button>
                            <Link to="/login" className="btn login-btn">
                                <i className="bi bi-person"></i>
                                <span>Đăng nhập</span>
                            </Link>
                        </div>
                    </nav>
                </div>
            </div>
            
            {/* Search Overlay */}
            <div className={`search-overlay ${searchActive ? 'active' : ''}`}>
                <div className="search-container">
                    <input 
                        type="text" 
                        placeholder="Tìm kiếm điểm đến, hoạt động..." 
                        ref={searchInputRef}
                    />
                    <button type="button" className="search-close" onClick={toggleSearch}>
                        <i className="bi bi-x-lg"></i>
                    </button>
                </div>
            </div>
        </header>
    );
};

export default Header;
