import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
// import 'bootstrap-icons/font/bootstrap-icons.css'; // Comment lại dòng này vì không có package bootstrap-icons
import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';
import { BrowserRouter } from 'react-router-dom';
import './styles/show/header.css';
import './styles/show/footer.css';
import './styles/HomePageCSS/HomePage.css'; // Đường dẫn đã được sửa

const style = document.createElement('style');
style.textContent = `
    .overlay {
        position: fixed;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        // background-color: rgba(0, 0, 0, 0.5);
        // z-index: 1040;
        backdrop-filter: blur(2px);
        transition: all 0.3s ease;
        opacity: 0;
        visibility: hidden;
    }
    
    .overlay.active {
        opacity: 1;
        visibility: visible;
    }
    
    .app-container {
        display: flex;
        flex-direction: column;
        min-height: 100vh;
        position: relative;
    }
    
    .main-content {
        flex: 1;
        padding-top: 1.5rem;
        padding-bottom: 1.5rem;
    }
`;
document.head.appendChild(style);

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
    <BrowserRouter>
        <App />
    </BrowserRouter>
);
