import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import '../../styles/show/header.css';

const Header = ({ menuOpen, setMenuOpen }) => {
    const [scrolled, setScrolled] = useState(false);
    const location = useLocation();
    const navigate = useNavigate();
    const { user, isAuthenticated, logout } = useAuth();

    // Xử lý sự kiện scroll
    useEffect(() => {
        const handleScroll = () => {
            if (window.scrollY > 50) {
                setScrolled(true);
            } else {
                setScrolled(false);
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    // Đóng menu khi thay đổi route
    useEffect(() => {
        setMenuOpen(false);
    }, [location, setMenuOpen]);

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    // Kiểm tra route hiện tại
    const isActive = (path) => {
        return location.pathname === path ? 'active' : '';
    };

    const handleLogout = () => {
        logout();
        navigate('/');
    };

    return (
        <header className={`header ${scrolled ? 'header-scrolled' : ''}`}>
            <div className="header-container">
                {/* Logo and Navigation Group */}
                <div className="logo-nav-group">
                    {/* Logo */}
                    <div className="logo">
                        <Link to="/" className="logo-link">
                            <div className="logo-wrapper">
                                <div className="logo-icon">
                                    <i className="bi bi-airplane-engines-fill"></i>
                                </div>
                                <div className="logo-content">
                                    <span className="logo-text">PHÚ YÊN</span>
                                </div>
                            </div>
                        </Link>
                    </div>

                    {/* Main Navigation */}
                    <nav className={`main-nav ${menuOpen ? 'active' : ''}`}>
                        <ul className="nav-links">
                            <li className="nav-item">
                                <Link to="/" className={`nav-link ${isActive('/')}`}>
                                    <i className="bi bi-house-door nav-icon"></i>
                                    <span>Trang chủ</span>
                                </Link>
                            </li>
                            <li className="nav-item">
                                <Link to="/locations" className={`nav-link ${isActive('/locations')}`}>
                                    <i className="bi bi-geo-alt nav-icon"></i>
                                    <span>Điểm đến</span>
                                </Link>
                            </li>
                            <li className="nav-item">
                                <Link to="/plan" className={`nav-link ${isActive('/plan')}`}>
                                    <i className="bi bi-calendar3 nav-icon"></i>
                                    <span>Lập kế hoạch</span>
                                </Link>
                            </li>
                            <li className="nav-item">
                                <Link to="/about" className={`nav-link ${isActive('/about')}`}>
                                    <i className="bi bi-info-circle nav-icon"></i>
                                    <span>Giới thiệu</span>
                                </Link>
                            </li>
                            <li className="nav-item">
                                <Link to="/contact" className={`nav-link ${isActive('/contact')}`}>
                                    <i className="bi bi-chat-dots nav-icon"></i>
                                    <span>Liên hệ</span>
                                </Link>
                            </li>
                        </ul>
                    </nav>
                </div>

                {/* Right Actions Group */}
                <div className="nav-actions">
                    <div className="nav-search">
                        <button className="search-btn" aria-label="Search">
                            <i className="bi bi-search"></i>
                        </button>
                    </div>

                    {isAuthenticated ? (
                        <div className="user-menu">
                            <button className="user-menu-btn">
                                <i className="bi bi-person-circle"></i>
                                <span>{user.username}</span>
                            </button>
                            <div className="user-dropdown">
                                <Link to="/profile" className="dropdown-item">
                                    <i className="bi bi-person"></i>
                                    Tài khoản
                                </Link>
                                <button onClick={handleLogout} className="dropdown-item text-danger">
                                    <i className="bi bi-box-arrow-right"></i>
                                    Đăng xuất
                                </button>
                            </div>
                        </div>
                    ) : (
                        <Link to="/login" className="login-btn">
                            <i className="bi bi-person-circle"></i>
                            <span>Đăng nhập</span>
                        </Link>
                    )}
                </div>

                {/* Mobile Toggle Button */}
                <button 
                    className={`mobile-toggle d-lg-none ${menuOpen ? 'active' : ''}`} 
                    onClick={toggleMenu}
                    aria-label="Toggle menu"
                >
                    <span></span>
                    <span></span>
                    <span></span>
                </button>
            </div>
        </header>
    );
};

export default Header;
