import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import MapSection from '../components/LocationDetail/MapSection';
import '../styles/LocationCSS/LocationDetailPage.css';

const LocationDetailPage = () => {
  const { id } = useParams();
  const [location, setLocation] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchLocationDetail = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/locations/${id}`);
        setLocation(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching location:', error);
        setLoading(false);
      }
    };

    fetchLocationDetail();
  }, [id]);

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">
          <i className="bi bi-arrow-repeat spinning"></i>
          <span>Đang tải dữ liệu...</span>
        </div>
      </div>
    );
  }

  if (!location) {
    return (
      <div className="error-container">
        <div className="error-message">
          <i className="bi bi-exclamation-circle"></i>
          <span>Không tìm thấy thông tin địa điểm</span>
        </div>
      </div>
    );
  }

  return (
    <div className="location-detail">
      <section className="location-header">
        <div 
          className="header-bg" 
          style={{
            backgroundImage: location.images?.length > 0 
              ? `url(http://localhost:5000${location.images[0]})` 
              : 'none'
          }}
        >
        </div>
        <div className="container">
          <div className="header-content">
            <h1 className="location-title">{location.title || 'Chưa có tiêu đề'}</h1>
            <div className="location-meta">
              <span className="meta-item">
                <i className="bi bi-geo-alt"></i>
                {location.address || 'Chưa có địa chỉ'}
              </span>
              <span className="meta-item">
                <i className="bi bi-star-fill"></i>
                {location.rating || '0'} ({location.reviews || '0'} đánh giá)
              </span>
              <span className="meta-item">
                <i className="bi bi-clock"></i>
                {location.openHours || 'Chưa có thông tin'}
              </span>
            </div>
          </div>
        </div>
      </section>

      <section className="location-main">
        <div className="container">
          <div className="row">
            <div className="col-lg-8">
              {location.images?.length > 0 && (
                <div className="content-section gallery-section">
                  <h2 className="section-title">Hình ảnh</h2>
                  <div className="gallery-grid">
                    {location.images.map((image, index) => (
                      <div key={index} className="gallery-item">
                        <img 
                          src={`http://localhost:5000${image}`} 
                          alt={`${location.title || 'Địa điểm'} ${index + 1}`} 
                        />
                      </div>
                    ))}
                  </div>
                </div>
              )}

              <div className="content-section">
                <h2 className="section-title">Giới thiệu</h2>
                <div className="description-content">
                  <p>{location.description || 'Chưa có mô tả'}</p>
                </div>
              </div>

              {location.features?.length > 0 && (
                <div className="content-section">
                  <h2 className="section-title">Điểm nổi bật</h2>
                  <div className="features-grid">
                    {location.features.map((feature, index) => (
                      <div key={index} className="feature-item">
                        <i className="bi bi-check-circle"></i>
                        <span>{feature}</span>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {location.latitude && location.longitude && (
                <div className="content-section">
                  <h2 className="section-title">Vị trí</h2>
                  <div className="map-container">
                    <MapSection 
                      latitude={location.latitude} 
                      longitude={location.longitude} 
                    />
                  </div>
                </div>
              )}
            </div>

            <div className="col-lg-4">
              <div className="location-sidebar">
                {/* Sidebar content */}
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default LocationDetailPage;
