import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../styles/LocationCSS/LocationListPage.css';

const LocationListPage = () => {
  const [locations, setLocations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeFilter, setActiveFilter] = useState('all');

  // Fetch data
  useEffect(() => {
    const fetchLocations = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/locations');
        if (!response.ok) throw new Error('Failed to fetch');
        const data = await response.json();
        setLocations(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };
    fetchLocations();
  }, []);

  const filterLocations = (category) => {
    setActiveFilter(category);
  };

  if (loading) return <div className="loader">Đang tải...</div>;
  if (error) return <div className="error-message">Lỗi: {error}</div>;

  return (
    <div className="locations-page">
      <div className="hero-section">
        <div className="hero-content">
          <h1>Khám Phá Phú Yên</h1>
          <p>Nơi bình minh đầu tiên trên đất Việt</p>
          <div className="search-bar">
            <input type="text" placeholder="Tìm kiếm địa điểm..." />
            <button><i className="bi bi-search"></i></button>
          </div>
        </div>
      </div>

      <div className="container">
        <div className="filter-container">
          <div className="filter-tabs">
            <button 
              className={`filter-tab ${activeFilter === 'all' ? 'active' : ''}`}
              onClick={() => filterLocations('all')}
            >
              <i className="bi bi-grid-fill"></i>
              <span>Tất cả</span>
            </button>
            <button 
              className={`filter-tab ${activeFilter === 'beach' ? 'active' : ''}`}
              onClick={() => filterLocations('beach')}
            >
              <i className="bi bi-water"></i>
              <span>Bãi biển</span>
            </button>
            <button 
              className={`filter-tab ${activeFilter === 'mountain' ? 'active' : ''}`}
              onClick={() => filterLocations('mountain')}
            >
              <i className="bi bi-mountain"></i>
              <span>Núi</span>
            </button>
            <button 
              className={`filter-tab ${activeFilter === 'culture' ? 'active' : ''}`}
              onClick={() => filterLocations('culture')}
            >
              <i className="bi bi-bank"></i>
              <span>Văn hóa</span>
            </button>
          </div>
          
          <div className="view-options">
            <button className="view-btn active">
              <i className="bi bi-grid-3x3"></i>
            </button>
            <button className="view-btn">
              <i className="bi bi-list"></i>
            </button>
          </div>
        </div>

        <div className="locations-grid">
          {locations.map(location => (
            <div className="location-card" key={location.id}>
              <div className="card-image">
                <img src={location.introduction?.image ? 
                  `http://localhost:5000${location.introduction.image}` : 
                  'https://via.placeholder.com/400x300'} 
                  alt={location.title} 
                />
                <div className="card-category">{location.category}</div>
                <div className="card-actions">
                  <button className="action-btn"><i className="bi bi-heart"></i></button>
                  <button className="action-btn"><i className="bi bi-share"></i></button>
                </div>
              </div>
              <div className="card-content">
                <h3>{location.title}</h3>
                <p>{location.subtitle}</p>
                <div className="card-meta">
                  <span><i className="bi bi-geo-alt"></i>{location.location}</span>
                  <span><i className="bi bi-star-fill"></i> 4.5</span>
                  <span><i className="bi bi-heart"></i> {location.likes || 0}</span>
                </div>
                <Link to={`/locations/${location.id}`} className="card-btn">
                  Chi tiết
                  <i className="bi bi-arrow-right"></i>
                </Link>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default LocationListPage;
